	<script type="text/javascript">
//orderan
var form_ajax = 'default';
$(document).ready(function() {
    $('#category').change(function() {
        var category = $('#category').val();
        $.ajax({
            type: "POST",
            url: "https://followersterbaik.com/ajax/order-get-service.php",
            data: "category=" + category,
            dataType: "html",
            success: function(data) {
                $('#service').html(data);
                $('#note').html('');
                $('#min-max-amount').html('');
                $('#price').val('0');
            },
            error: function() {
                $('#ajax-result').html(
                    '<font color="red">Terjadi kesalahan! Silahkan refresh halaman.</font>'
                    );
            }
        });
    });
    $('#service').change(function() {
        var service = $('#service').val();
        $.ajax({
            type: "POST",
            url: "https://followersterbaik.com/ajax/order-select-service.php",
            data: "service=" + service + "&category=" + $('#category').val(),
            dataType: "json",
            success: function(data) {
                $('#form-replaceable').html(data.html);
                console.log(data.html);
                $('#note').html(data.note);
                $('#min-max-amount').html(data.min_max);
                $('#price').val(data.price);
            },
            error: function() {
                $('#ajax-result').html(
                    '<font color="red">Terjadi kesalahan! Silahkan refresh halaman.</font>'
                    );
            }
        }).done(function(e) {
            console.log(e.form);
            form_ajax = e.form;
            $('#quantity').keyup(function() {
                var quantity = $('#quantity').val(),
                    rate = $('#price').val() / 1000;
                $('#total-price').val(quantity * rate);
            });
            $('#komen').keyup(function() {
                var area = document.getElementById("komen")
                var text = area.value.replace(/\s+$/g, "")
                var split = text.split("\n")
                //return split.length
                $('#quantity').val(split.length);
                var quantity = $('#quantity').val(),
                    rate = $('#price').val() / 1000;
                $('#total-price').val(quantity * rate);
            });
        });
    });


    $('#order-form').on('submit', function(ev) {
        if ($('#category').val() != 39) {
            $('#order-form').submit();
        } else {
            console.log('hooray!!');
            $('.otw-disabled').prop('disabled', true);
            $.ajax({
                type: "POST",
                url: 'ajax/order-pulsa.php',
                data: $('#order-form').serialize(),
                dataType: 'json',
                success: function(data) {
                    $('#alert-message').removeAttr('class');
                    $('#alert-message').attr('class', 'alert alert-dismissable');
                    $('#alert-message').addClass('alert-' + data.alert);
                    $('#msg-title').html(data.title);
                    $('#msg-body').html(data.msg);
                    $('#alert-message').fadeIn('fast');
                },
                error: function() {
                    $('#ajax-result').html(
                        '<font color="red">Terjadi kesalahan! Silahkan refresh halaman.</font>'
                        );
                }
            }).done(function(d) {
                $('.otw-disabled').prop('disabled', false);
            });
        }
        ev.preventDefault();
    });

});
// end orderan

